mapper.update:
UPDATE message
SET message_status = #{messageStatus}
WHERE message_key = #{messageKey} AND message_status = #{originalMessageStatus}

UPDATE message
SET send_status = #{sendStatus}
WHERE message_key = #{messageKey} AND send_status = #{originalSendStatus}

UPDATE message
SET verify_try_count = #{verifyTryCount}, verify_next_retry_time = #{verifyNextRetryTime}
WHERE message_key = #{messageKey}

UPDATE message
SET send_try_count = #{sendTryCount}, send_next_retry_time = #{sendNextRetryTime}
WHERE message_key = #{messageKey}
